# sticking to rails and rspec-rails APIs to keep things simple and stable.

RSpec.describe "/orders", type: :request do
  # Order. As you add validations to Order, be sure to
  # adjust the attributes here as well.
  let(:food) { create(:food) }
  let(:drink) { create(:drink) }
  let(:valid_attributes) {
    { items_attributes: [
      { food_id: food.id, name: food.name, price: 13.23, quantity: 2 },
      { drink_id: drink.id, name: drink.name, price: 4.50, quantity: 3 },
    ] }
  }

  let(:invalid_attributes) {
    { items_attributes: [
      { food_id: food.id, name: food.name, price: 13.23, quantity: -1 },
      { drink_id: drink.id, name: drink.name, price: 4.50, quantity: 3 },
    ] }
  }

  describe "GET /index" do
    it "renders a successful response" do
      Order.create! valid_attributes
      get orders_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      order = Order.create! valid_attributes
      get order_url(order)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_order_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "render a successful response" do
      order = Order.create! valid_attributes
      get edit_order_url(order)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Order" do
        expect {
          post orders_url, params: { order: valid_attributes }
        }.to change(Order, :count).by(1)
      end

      it "redirects to the created order" do
        post orders_url, params: { order: valid_attributes }
        expect(response).to redirect_to(order_url(Order.last))
      end
    end

    context "with invalid parameters" do
      it "does not create a new Order" do
        expect {
          post orders_url, params: { order: invalid_attributes }
        }.to change(Order, :count).by(0)
      end

      it "renders a successful response (i.e. to display the 'new' template)" do
        post orders_url, params: { order: invalid_attributes }
        expect(response).to render_template(:new)
      end
    end
  end

  describe "PATCH /update" do
    let(:order) { Order.create(valid_attributes) }

    context "with valid parameters" do
      let(:new_attributes) {
        { items_attributes: [
          { id: order.items.first.id, food_id: food.id, name: food.name, price: 13.23, quantity: 1 },
          { id: order.items.second.id, drink_id: drink.id, name: drink.name, price: 4.50, quantity: 3 },
        ] }
      }

      it "updates the requested order" do
        patch order_url(order), params: { order: new_attributes }
        order.reload
        expect(order.items.first.quantity).to eq(1)
      end

      it "redirects to the order" do
        patch order_url(order), params: { order: new_attributes }
        order.reload
        expect(response).to redirect_to(order_url(order))
      end
    end

    context "with invalid parameters" do
      it "renders a successful response (i.e. to display the 'edit' template)" do
        order = Order.create! valid_attributes
        patch order_url(order), params: { order: invalid_attributes }
        expect(response).to render_template(:edit)
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested order" do
      order = Order.create! valid_attributes
      expect {
        delete order_url(order)
      }.to change(Order, :count).by(-1)
    end

    it "redirects to the orders list" do
      order = Order.create! valid_attributes
      delete order_url(order)
      expect(response).to redirect_to(orders_url)
    end
  end
end
